mapper.update:

	UPDATE 
		ITEM 
	SET 
		ITEM_SHORTPATH = #{ITEM_SHORTPATH} 
	WHERE 
		ITEM_NO=#{ITEM_NO}


	UPDATE 
		ITEM 
	SET 
		ITEM_IMAGEPATH = #{ITEM_IMAGEPATH} 
	WHERE 
		ITEM_NO=#{ITEM_NO}


	UPDATE 
		KSM_COMMENT 
	SET 
		COM_CONTENT = #{COM_CONTENT} 
	WHERE 
		COM_NO = #{COM_NO}
